import axios from 'axios'   
import { withRouter } from 'next/router';
const UserDetail = (props)=>{
    console.log('Props with user detail', props); 
    const {router } = props;
    // console.log('router ',router)
    return (
        <>
            <h2> User details </h2>
            <p> {JSON.stringify(props.user)}</p>
        </>

    )
}

UserDetail.getInitialProps = async (context)=>{
    // console.log('Context', context.query)
    let {id} = context.query
    let user = await axios.get(`https://jsonplaceholder.typicode.com/users?id=${id}`)
    console.log('Querying data on server' , user.data[0]);
    return(
        {user: user.data[0]}
    );
} 

export default withRouter(UserDetail);
